package com.CoreJavaProgramming;

public class VowelsAndConsonent {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char alphabet = 'z';
		switch(alphabet) {
		case 'a': System.out.println(alphabet + " is vowel");
		break;
		case 'e':System.out.println(alphabet + " is vowel");
		break;
		case 'i':System.out.println(alphabet + " is vowel");
		break;
		case 'o':System.out.println(alphabet + " is vowel");
		break;
		case 'u':System.out.println(alphabet + " is vowel");
		break;
		default:System.out.println(alphabet + " is consonent");	
		}

	}

}
